from Crypto.Util.number import getPrime, long_to_bytes, bytes_to_long

p,q = getPrime(512), getPrime(512)

n = p * q

message = b"REDACTED"
m = bytes_to_long(message)

e = 7

c = pow(m, e, n)

print(f"truncated message = {message[:-15]}")
print(f"n = {n}")
print(f"c = {c}")

